import axios from 'axios';
import React, { useEffect, useState } from 'react';

import { View, Text, TextInput, TouchableHighlight, StyleSheet, Image } from 'react-native';





export function EditUser(props) {

    const [nombre, setNombre] = useState("");
    const id = route.params.id;
    const[nuevo, setNuevo] = useState("");


    axios.get("http://192.168.1.127:8080/api/v2/user/"+ id)
    .then((res) => {
        setNombre(res.data.username);
       
    })
    .catch(error=>{
        alert("Error server "+error)
      })


      function Cambiar(){
          axios.put("http://192.168.1.127:8080/api/v2/user/"+ id + nuevo)
          .then((res) =>{
              alert("User cambiado")
          })
          .catch(error=>{
            alert("Error server "+error)
          })
      }


    return (
        <View style={styles.container}>
            <View style={styles.header}>
                <Image style={{ width: 100, height: 100, alignItems: 'center' }} source={require('../assets/def.png')} />
                <Text
                    style={{ color: '#333333', fontWeight: 'bold', fontFamily: 'Baskerville-Bold', fontSize: 30 }}>
                    Hi-Go!
                </Text>
            </View>
            <View style={styles.user}>
                <Image style={{ top: 20, left: 20, width: 150, height: 150, alignItems: 'center' }} source={require('../assets/Avatar.png')} />
                <View style={styles.info}>
                    <Text style={styles.texto}>Username: {nombre}</Text>
                    <TextInput
                       style={styles.input}
                       placeholder="Username..."
                       value={nuevo}
                       onChangeText={(text) => setNuevo(text)}
            />            
            <TouchableHighlight style={styles.button} onPress={() => Cambiar()}>
            <Text style={styles.textButton}>Cambiar</Text>
            </TouchableHighlight>


                </View>
            </View>
        </View>
    );
};

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#3EA9FA',
        // alignItems: 'center',
        justifyContent: 'space-around',
        padding: 15
    },
    header: {
        justifyContent: 'space-around',
        flexDirection: 'row',
        alignItems: 'center',
    },
    user: {
        backgroundColor: '#FEFAE0',
        height: 700
    },
    info: {
        left: 70,
        top: 70,
        marginBottom: 20
    },
    texto: {
        marginBottom: 5,
        fontSize: 25,
        fontWeight: 'bold',
        fontFamily: 'Times New Roman'
    },
    button: {
        alignSelf: 'center',
        width: 350,
        height: 60,
        bottom: 15,
        top: 130,
        borderRadius: 50,
        backgroundColor: '#333333',
        alignItems: 'center',
        justifyContent: 'center',
        marginBottom: 25
    },
    textButton: {
        color: '#FEFAE0',
        fontWeight: 'bold',
        fontFamily: 'AmericanTypewriter-Bold',
        fontSize: 40
    }
});
export default EditUser;
